mapper.select:
        select
        id, sku_id, original_price, paid_price, order_id, status_id, count, deleted, create_time, update_time
        from greenorange.tb_order_goods
        
            
                and id = #{id}
            
            
                and sku_id = #{skuId}
            
            
                and original_price = #{originalPrice}
            
            
                and paid_price = #{paidPrice}
            
            
                and order_id = #{orderId}
            
            
                and status_id = #{statusId}
            
            
                and count = #{count}
            
            
                and deleted = #{deleted}
